package test

import (
	"bytes"
	"encoding/hex"
	"fmt"
	"github.com/leverwwz/go-substrate-rpc-client/scale"
	"github.com/leverwwz/go-substrate-rpc-client/types"
	"regexp"
	"testing"
)

func Test_DecodeU32(t *testing.T) {
	src := "20df0e0b00000000"
	data, _ := hex.DecodeString(src)
	var u types.U32
	decoder := scale.NewDecoder(bytes.NewReader(data))
	decoder.Decode(&u)
	fmt.Println(u)
}

func Test_DecodeU128(t *testing.T) {
	s := "Option<U8>"
	reg := regexp.MustCompile("^([^<]*)<(.+)>$")
	typeParts := reg.FindStringSubmatch(s)
	fmt.Println(typeParts)
}

func Test_DecodeBytes(t *testing.T) {
	src := ""
	data, _ := hex.DecodeString(src)
	fmt.Println(len(data))
	// 3*16+13 = 48+13 = 61
	decoder := scale.NewDecoder(bytes.NewReader(data))
	var b types.Bytes
	decoder.Decode(&b)
	fmt.Println(len(b))
	fmt.Println(hex.EncodeToString(b))
}
